mapper.select:
select *
from orders
where number = #{orderNumber}
  and user_id = #{userId};

select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and zhuanyuanid = #{zhuanyuanId}


order by order_time desc

SELECT id
    FROM zhuanyuan
    WHERE userid = #{userId}

select sum(amount) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select count(id) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select od.name name,sum(od.number) number from order_detail od ,orders o where od.order_id = o.id and o.status = 5
and order_time >= #{begin}
and order_time <= #{end}
group by name order by number desc limit 0, 10

mapper.update:
UPDATE orders
SET commentid = #{id} ,
  judge = #{judge}
WHERE id = #{orderid}

UPDATE zhuanyuan
SET judge = judge + #{judge}
WHERE id = (SELECT zhuanyuanid FROM orders WHERE id = #{orderid})
